import React, { useState, useEffect } from 'react';
import { getSummary, getDistrictStats } from '../services/api';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, PieChart, Pie, Cell, ResponsiveContainer } from 'recharts';

// „ÉÅ„É£„Éº„ÉàÁî®„Ç´„É©„Éº„Éë„É¨„ÉÉ„Éà
const COLORS = ['#0088FE', '#00C49F', '#FFBB28', '#FF8042'];

function Dashboard() {
  const [data, setData] = useState({
    summary: null,
    districts: null,
    loading: true,
    error: null
  });

  // „Éá„Éº„ÇøÂèñÂæóÈñ¢Êï∞
  const fetchData = async () => {
    setData(prev => ({ ...prev, loading: true }));
    
    try {
      // Êú¨Áï™APIÂëº„Å≥Âá∫„Åó
      const summaryData = await getSummary();
      
      // „É¢„ÉÉ„ÇØ„Éá„Éº„Çø„Åß‰ª£ÊõøÔºàCORSÂïèÈ°åÂõûÈÅøÔºâ
      const mockDistrictData = {
        districts: {
          'Êà∏Â°öÂå∫': {
            totalPosters: 331,
            byStatus: { 'Êú™ÁùÄÊâã': 331, 'ÂÆå‰∫Ü': 0 },
            completionRate: 0
          }
        }
      };
      
      setData({
        summary: summaryData,
        districts: mockDistrictData,
        loading: false,
        error: null
      });
    } catch (error) {
      setData(prev => ({
        ...prev,
        loading: false,
        error: '„Éá„Éº„ÇøÂèñÂæó„Å´Â§±Êïó„Åó„Åæ„Åó„Åü'
      }));
    }
  };

  // ÂàùÂõû„Éá„Éº„ÇøÂèñÂæó
  useEffect(() => {
    fetchData();
  }, []);

  // „ÉÅ„É£„Éº„ÉàÁî®„Éá„Éº„ÇøÂ§âÊèõ
  const getChartData = () => {
    if (!data.summary?.byDistrict) return [];
    
    return Object.entries(data.summary.byDistrict).map(([district, count]) => ({
      name: district,
      value: count,
      completed: 0, // ÂÆå‰∫ÜÊï∞ÔºàÁèæÂú®„ÅØ0Ôºâ
      pending: count // Êú™ÂÆå‰∫ÜÊï∞
    }));
  };

  const getPieData = () => {
    if (!data.summary?.byStatus) return [];
    
    return Object.entries(data.summary.byStatus).map(([status, count]) => ({
      name: status,
      value: count
    }));
  };

  if (data.loading) {
    return (
      <div style={{ textAlign: 'center', padding: '50px' }}>
        <h2>üìä „Éá„Éº„ÇøË™≠„ÅøËæº„Åø‰∏≠...</h2>
      </div>
    );
  }

  if (data.error) {
    return (
      <div style={{ textAlign: 'center', padding: '50px', color: 'red' }}>
        <h2>‚ùå {data.error}</h2>
        <button onClick={fetchData}>ÂÜçË©¶Ë°å</button>
      </div>
    );
  }

  return (
    <div style={{ padding: '20px', maxWidth: '1200px', margin: '0 auto' }}>
      <h1>üéØ Ê®™Êµú„Éù„Çπ„Çø„ÉºÁÆ°ÁêÜ„Ç∑„Çπ„ÉÜ„É†</h1>
      
      {/* Áµ±Ë®à„Çµ„Éû„É™„Éº */}
      <div style={{ 
        display: 'grid', 
        gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', 
        gap: '20px',
        marginBottom: '30px'
      }}>
        <div style={{ 
          background: '#f8f9fa', 
          padding: '20px', 
          borderRadius: '8px',
          textAlign: 'center'
        }}>
          <h3>üìç Á∑è„Éù„Çπ„Çø„ÉºÊï∞</h3>
          <p style={{ fontSize: '24px', margin: '10px 0' }}>
            {data.summary?.totalPosters?.toLocaleString() || 0}‰ª∂
          </p>
        </div>
        
        <div style={{ 
          background: '#f8f9fa', 
          padding: '20px', 
          borderRadius: '8px',
          textAlign: 'center'
        }}>
          <h3>‚úÖ ÂÆå‰∫ÜÁéá</h3>
          <p style={{ fontSize: '24px', margin: '10px 0' }}>
            {data.summary?.completionRate || 0}%
          </p>
        </div>
        
        <div style={{ 
          background: '#f8f9fa', 
          padding: '20px', 
          borderRadius: '8px',
          textAlign: 'center'
        }}>
          <h3>üè¢ ÂØæË±°Âå∫Êï∞</h3>
          <p style={{ fontSize: '24px', margin: '10px 0' }}>
            {Object.keys(data.summary?.byDistrict || {}).length}Âå∫
          </p>
        </div>
      </div>

      {/* „ÉÅ„É£„Éº„Éà„Çª„ÇØ„Ç∑„Éß„É≥ */}
      <div style={{ 
        display: 'grid', 
        gridTemplateColumns: 'repeat(auto-fit, minmax(400px, 1fr))', 
        gap: '20px',
        marginTop: '30px'
      }}>
        {/* Âå∫Âà•ÈÄ≤Êçó„ÉÅ„É£„Éº„Éà */}
        <div style={{ background: '#f8f9fa', padding: '20px', borderRadius: '8px' }}>
          <h3>üìä Âå∫Âà•ÈÄ≤Êçó„ÉÅ„É£„Éº„Éà</h3>
          <ResponsiveContainer width="100%" height={250}>
            <BarChart data={getChartData()}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="name" />
              <YAxis />
              <Tooltip />
              <Bar dataKey="value" fill="#0088FE" />
            </BarChart>
          </ResponsiveContainer>
        </div>

        {/* „Çπ„ÉÜ„Éº„Çø„ÇπÂà•ÂÜÜ„Ç∞„É©„Éï */}
        <div style={{ background: '#f8f9fa', padding: '20px', borderRadius: '8px' }}>
          <h3>ü•ß „Çπ„ÉÜ„Éº„Çø„ÇπÂà•ÂàÜÂ∏É</h3>
          <ResponsiveContainer width="100%" height={250}>
            <PieChart>
              <Pie
                data={getPieData()}
                cx="50%"
                cy="50%"
                outerRadius={80}
                fill="#8884d8"
                dataKey="value"
                label={({ name, value }) => `${name}: ${value}`}
              >
                {getPieData().map((entry, index) => (
                  <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                ))}
              </Pie>
              <Tooltip />
            </PieChart>
          </ResponsiveContainer>
        </div>
      </div>

      {/* Âå∫Âà•Ë©≥Á¥∞ */}
      <div style={{ background: '#f8f9fa', padding: '20px', borderRadius: '8px' }}>
        <h2>üìä Âå∫Âà•ÈÄ≤ÊçóÁä∂Ê≥Å</h2>
        {Object.entries(data.summary?.byDistrict || {}).map(([district, count]) => (
          <div key={district} style={{ 
            display: 'flex', 
            justifyContent: 'space-between',
            padding: '10px 0',
            borderBottom: '1px solid #ddd'
          }}>
            <span>{district}</span>
            <span>{count.toLocaleString()}‰ª∂</span>
          </div>
        ))}
      </div>

      <div style={{ textAlign: 'center', marginTop: '30px' }}>
        <button 
          onClick={fetchData}
          style={{
            background: '#007bff',
            color: 'white',
            border: 'none',
            padding: '10px 20px',
            borderRadius: '5px',
            cursor: 'pointer'
          }}
        >
          üîÑ „Éá„Éº„ÇøÊõ¥Êñ∞
        </button>
      </div>
    </div>
  );
}

