import React, { useState, useEffect } from 'react';
import { getSummary, getDistrictStats } from '../services/api';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, PieChart, Pie, Cell, ResponsiveContainer } from 'recharts';

function Dashboard() {
  const [data, setData] = useState({
    summary: null,
    districts: null,
    loading: true,
    error: null
  });

  // „Éá„Éº„ÇøÂèñÂæóÈñ¢Êï∞
  const fetchData = async () => {
    setData(prev => ({ ...prev, loading: true }));
    
    try {
      const summaryData = await getSummary();
      const districtData = await getDistrictStats();
      
      setData({
        summary: summaryData,
        districts: districtData,
        loading: false,
        error: null
      });
    } catch (error) {
      setData(prev => ({
        ...prev,
        loading: false,
        error: '„Éá„Éº„ÇøÂèñÂæó„Å´Â§±Êïó„Åó„Åæ„Åó„Åü'
      }));
    }
  };

  // ÂàùÂõû„Éá„Éº„ÇøÂèñÂæó
  useEffect(() => {
    fetchData();
  }, []);

  if (data.loading) {
    return (
      <div style={{ textAlign: 'center', padding: '50px' }}>
        <h2>üìä „Éá„Éº„ÇøË™≠„ÅøËæº„Åø‰∏≠...</h2>
      </div>
    );
  }

  if (data.error) {
    return (
      <div style={{ textAlign: 'center', padding: '50px', color: 'red' }}>
        <h2>‚ùå {data.error}</h2>
        <button onClick={fetchData}>ÂÜçË©¶Ë°å</button>
      </div>
    );
  }

  return (
    <div style={{ padding: '20px', maxWidth: '1200px', margin: '0 auto' }}>
      <h1>üéØ Ê®™Êµú„Éù„Çπ„Çø„ÉºÁÆ°ÁêÜ„Ç∑„Çπ„ÉÜ„É†</h1>
      
      {/* Áµ±Ë®à„Çµ„Éû„É™„Éº */}
      <div style={{ 
        display: 'grid', 
        gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', 
        gap: '20px',
        marginBottom: '30px'
      }}>
        <div style={{ 
          background: '#f8f9fa', 
          padding: '20px', 
          borderRadius: '8px',
          textAlign: 'center'
        }}>
          <h3>üìç Á∑è„Éù„Çπ„Çø„ÉºÊï∞</h3>
          <p style={{ fontSize: '24px', margin: '10px 0' }}>
            {data.summary?.totalPosters?.toLocaleString() || 0}‰ª∂
          </p>
        </div>
        
        <div style={{ 
          background: '#f8f9fa', 
          padding: '20px', 
          borderRadius: '8px',
          textAlign: 'center'
        }}>
          <h3>‚úÖ ÂÆå‰∫ÜÁéá</h3>
          <p style={{ fontSize: '24px', margin: '10px 0' }}>
            {data.summary?.completionRate || 0}%
          </p>
        </div>
        
        <div style={{ 
          background: '#f8f9fa', 
          padding: '20px', 
          borderRadius: '8px',
          textAlign: 'center'
        }}>
          <h3>üè¢ ÂØæË±°Âå∫Êï∞</h3>
          <p style={{ fontSize: '24px', margin: '10px 0' }}>
            {Object.keys(data.summary?.byDistrict || {}).length}Âå∫
          </p>
        </div>
      </div>

      {/* Âå∫Âà•Ë©≥Á¥∞ */}
      <div style={{ background: '#f8f9fa', padding: '20px', borderRadius: '8px' }}>
        <h2>üìä Âå∫Âà•ÈÄ≤ÊçóÁä∂Ê≥Å</h2>
        {Object.entries(data.summary?.byDistrict || {}).map(([district, count]) => (
          <div key={district} style={{ 
            display: 'flex', 
            justifyContent: 'space-between',
            padding: '10px 0',
            borderBottom: '1px solid #ddd'
          }}>
            <span>{district}</span>
            <span>{count.toLocaleString()}‰ª∂</span>
          </div>
        ))}
      </div>

      <div style={{ textAlign: 'center', marginTop: '30px' }}>
        <button 
          onClick={fetchData}
          style={{
            background: '#007bff',
            color: 'white',
            border: 'none',
            padding: '10px 20px',
            borderRadius: '5px',
            cursor: 'pointer'
          }}
        >
          üîÑ „Éá„Éº„ÇøÊõ¥Êñ∞
        </button>
      </div>
    </div>
  );
}

export default Dashboard;
